loginUrl:
// 接口设置，以及链接
var sz = source.getLoginHeader();
var data = [
    ["DS", "DeepSeek"],
    ["豆包", "doubao"],
    ["智脑", "zhinao"],
    ["通义", "tongyi"],
    ["混元", "hunyuan"],
    ["文心", "wenxin"],
    ["Max", "MiniMax"],
    ["智谱", "zhipu"],
    ["商汤", "shangtang"],
    ["零一", "lingyi"],
    ["百小应", "baixiaoying"],
    ["星火", "xunfei"],
    ["星辰", "stepspark"],
    ["Kimi", "Kimi"],
]; // 接口数据

// 判断接口是否为空
if (sz == null || sz == "" || isNaN(sz) || sz < 0 || sz > data.length) {
    sz = 0;
    source.putLoginHeader(sz)
    java.toast("未设置接口或接口无效，使用默认接口：" + data[sz][0]);
} else {
    sz = parseInt(sz); // 将sz转换为整数
}

// 设置保存接口
function set(num) {
    if (num < 0 || num > data.length) {
        java.longToast("无效的接口编号！");
        return;
    }
    var show = "设置成功！\n当前接口：";
    var tips = "\n接口已更改";
    var msg = show + data[num][0] + tips;
    java.longToast(msg);
    return source.putLoginHeader(num)
}

// 获取接口显示
function get(sz) {
    if (sz < 0 || sz > data.length) {
        java.longToast("无效的接口编号！");
        return;
    }
    var show = "\n当前接口：\n";
    return java.longToast(show + data[sz][0]);
}

// 按接口获取网址
var api = data[sz][1];
api;
